package com.assignment.app;

import java.util.Scanner;

public class Q8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num1 = 10;
		int num2 = 5;
		int temp = 0;

		System.out.println("Before Swapping, the Numbers are: " + num1 + ","
				+ num2);

		temp = num1;
		num1 = num2;
		num2 = temp;

		System.out.println("After Swapping, the Numbers are: " + num1 + ","
				+ num2);

	}

}
